import { createContext, ReactNode,  useState } from "react";

interface ModalProviderProps{
    children:ReactNode
}
interface ModalProviderData{
    openModal : (children:Children) => void,
    closeModal: () => void,
    actualChildren: Children,
    isOpen: boolean
}
interface Children{
    balance:[],
    email:string,
    id:number,
    parentId:number,
    wallet:number,
    wishlist:[],
    name:string,
    type: string
}

export const ModalContext = createContext <ModalProviderData>({} as ModalProviderData)

export const ModalProvider = ({ children }: ModalProviderProps) =>{
    const [ actualChildren, setActualChildren ] = useState<Children>({} as Children)
    const [ isOpen, setIsOpen ] = useState(false)
    const openModal = (children:Children) =>{
        setIsOpen(true)
        setActualChildren(children)
    }


    const closeModal = () =>{
        setIsOpen(false)
    }
    return(
        <ModalContext.Provider value={{ openModal, isOpen, actualChildren, closeModal }}>
            { children }
        </ModalContext.Provider>
    )
}
