import { Form } from './style'
import { IoMdClose } from "react-icons/io";
import { useContext } from 'react';
import { ModalContext } from '../../Providers/Modal';
import * as yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import { ActivitiesContext } from '../../Providers/Activities';

interface FormProps {
    achivied: boolean
    frequency: string
    name: string
    reward: number
    userId: number,
    id?:number
}
const FormCreatingActivity = () =>{
    const { register, handleSubmit } = useForm()
    const { openModal } = useContext(ModalContext)
    const { createActivie} = useContext(ActivitiesContext)

    const onSubmitFunction = (data:FormProps) =>{
        console.log(data)
        data.userId=Number(localStorage.getItem('userId'));
        data.achivied=false 
        createActivie(data)

    }
    return(
        <Form onSubmit={handleSubmit(onSubmitFunction)}
            initial={{ scale: 0, opacity:0 }}
            animate={{ scale: 1,opacity:1 }}
            transition={{
            type: "spring",
            stiffness: 80,

            }}
        >
            <div>
                <h2>Adicione uma nova atividade</h2>
                <button onClick={()=>openModal()}><IoMdClose/></button>
            </div>
            <input type="text"   placeholder='Insira o Nome' {...register('name')}/>
            <input type="number"  placeholder='Insira o valor'{...register('reward')}/>
            <input type="text"   placeholder='Insira a frequÃªncia'{...register('frequency')}/>
            <button className='add-button'  type='submit'> Adicionar</button>
        </Form>
    )
}
export default FormCreatingActivity
