import { Form } from './style'
import { IoMdClose } from "react-icons/io";
import { useContext } from 'react';
import * as yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import { ActivitiesContext } from '../../Providers/Activities';

interface FormProps {
    achivied: boolean
    frequency: string
    name: string
    reward: number
    userId: number,
    id?:number
}

interface FormCreatingActivity{
    handleEditing: () => void
}
const FormCreatingActivity = ({ handleEditing}:FormCreatingActivity) =>{

    const schema = yup.object().shape({
        name: yup.string().required("Nome da atividade obrigat贸rio"),
        reward: yup.string().required("Recompensa da atividade obrigat贸ria"),

    })
    const { register, handleSubmit, formState:{errors} } = useForm({
        resolver: yupResolver(schema)
    })
    const { createActivie,getYourChildrens } = useContext(ActivitiesContext)

    const onSubmitFunction = (data:FormProps) =>{
        console.log(data)

        data.achivied=false 
        createActivie(data)
        getYourChildrens()

    }
    return(
        <Form onSubmit={handleSubmit(onSubmitFunction)}
            initial={{ scale: 0, opacity:0 }}
            animate={{ scale: 1,opacity:1 }}
            transition={{
            type: "spring",
            stiffness: 80,

            }}
        >
            <div>
                <h2>Adicione uma nova atividade</h2>
                <button ><IoMdClose/></button>
            </div>
            <section className='input-container'>
                <p>{errors.name?.message}</p>
                <input type="text"   placeholder='Insira o Nome' {...register('name')}/>
            </section>
            <section className='input-container'>
                <p>{errors.name?.message}</p>
                <input type="number"  placeholder='Insira o valor'{...register('reward')}/>
            </section>
            <select {...register("frequency")} >
                <option value="Diaria">Diaria</option>
                <option value="Semanal">Semanal</option>
                <option value="Mensal">Mensal</option>
            </select>
            <button className='add-button'  type='submit' onClick={handleEditing}> Adicionar</button>
        </Form>
    )
}
export default FormCreatingActivity
