import { useContext, useEffect, useState } from "react"
import { ActivitiesContext } from "../../Providers/Activities"
import { Container, InfoContainer }from "./style.js"
import api from '../../Services/api'
interface Children{
    balance:[],
    email:string,
    id:number,
    parentId:number,
    password:string,
    wallet:[],
    wishlist:[],
    name:string
}

interface CardChildrenProps{
    children:Children
}

const CardChildren = ({children}:CardChildrenProps) =>{
    const [childrenActivies, setChildrenActivities ] = useState([])

    const getYourActivities = (userId:number) =>{
        api
         .get(`activities/?userId=${userId}`,{
            headers:{
                Authorization:`Bearer ${localStorage.getItem('token')}`
            }
        })
          .then((response)=>{
              setChildrenActivities(response.data)
          })
           .catch((err)=>console.log('geyourActivies', err))
    }
    useEffect(()=>{
        getYourActivities(children.id)
    },[])

    console.log(childrenActivies)
    return(
        <Container>
            <InfoContainer>
                <p>{children.name}</p>
            </InfoContainer>
        </Container>
    )
}
export default CardChildren
