import styled from 'styled-components'

export const Container = styled.div`
    width: 350px;
    margin: 10px;
    background-color: #302F4D;
    display: flex;
    flex-direction: column;
    img{
        width: 200px;
        height: 200px;
        border-radius: 50%;
    }
    p{
        font-size:18px;
    }
`

export const InfoContainer = styled.div`
    display: flex;
`
