import styled from "styled-components";

export const Form = styled.form`
    width: 350px;
    border: 1px solid black;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    input{
        width: 300px ;
        margin: 20px;
        height: 48px;
    }
`
