import { useContext, useEffect, useState } from "react"
import { ActivitiesContext } from "../../Providers/Activities"

interface Children{
    balance:[],
    email:string,
    id:number,
    parentId:number,
    password:string,
    wallet:[],
    wishlist:[],
    name:string
}

interface CardChildrenProps{
    children:Children
}
const CardChildren = ({children}:CardChildrenProps) =>{
    const { getYourActivities } = useContext(ActivitiesContext)
    const [childrenActivies, setChildrenActivities ] = useState([])
    useEffect(()=>{
        setChildrenActivities(getYourActivities(children.id), setChildrenActivities)
    },[])
    return(
        <div>{children.name}</div>
    )
}
export default CardChildren
