import { useForm } from 'react-hook-form'
import { Form } from './style.js'
import { IoMdClose } from "react-icons/io";
import { useContext } from 'react';
import { ModalContext } from '../../Providers/Modal';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup'

interface FormData{
    name:string,
    password:string,
    confirmPassword:string
}

const FormEditingProfile = () =>{
    const { handleEditingProfile } = useContext(ModalContext)

    const schema = yup.object().shape({
        name:yup.string().required("Nome obrigat贸rio").max(13, "Permitido apenas 13 caracteres"),
        password: yup.string().required("Senha obrigat贸ria"),
        confirmPassword: yup.string().oneOf([yup.ref("password")], "Senhas diferentes")

    })
    const { register, handleSubmit, formState:{ errors } } = useForm({
        resolver:yupResolver(schema)
    })


    const onSubmitFunction = (data:FormData) =>{
        console.log(data)
    }
    return(
        <Form onSubmit={handleSubmit(onSubmitFunction)}
            initial={{ scale: 0, opacity:0 }}
            animate={{ scale: 1,opacity:1 }}
            transition={{
            type: "spring",
            stiffness: 80,

            }}
        >
            <div>
                <h2>Editar Perfil</h2>
                <button onClick={handleEditingProfile}><IoMdClose/></button>
            </div>
            <section>
                <p>{errors.name?.message}</p>
                <input type='text' placeholder='Editar Nome'{...register('name')}/>
            </section>
            <section>
                <p>{errors.password?.message}</p>
                <input type='password' placeholder='Editar senha'{...register('password')}/>

            </section>
            <section>
                <p>{errors.confirmPassword?.message}</p>
                <input type='password' placeholder='Confirmar senha'{...register('confirmPassword')}/>
            </section>

            <button className='add-button'>Editar</button>
        </Form>
    )
}

export default FormEditingProfile
