import React from "react";
import styled from "styled-components";
import { toast, ToastContainer } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";
import svgHappy from '../../assets/images/happy.png'
interface ToastProps{
    type:string,
    message:string 
}
const Toast = styled(ToastContainer)`
  .Toastify__toast--info {
    background: "rgb(51, 102, 255)";
  }
  .Toastify__toast--success {
    background: "rgb(51, 187, 102)";
  }
  .Toastify__toast--warning {
    background: "rgb(254, 255, 20)";
  }
  .Toastify__toast--error {
    background: "rgb(255, 102, 102)";
  }
`;
export const showToast = ({ type, message }: ToastProps) => {
  switch (type) {
    case "success":
      toast.success(message,{
        icon: () =>  <img src={svgHappy} alt='happy kid'/>
      });
      break;
    case "warn":
      toast.warn(message);
      break;
    case "error":
      toast.error(message);
      break;
    default:
      toast.info(message);
  }
};
export const ToastAnimated = () => {
  return <Toast/>;
}
